    <v-dialog
            v-model="dialog"
            max-width="50%"
            >
      <area-popup :post-id="terId" :post-designation="terDesignation" :post-population="terPopulation"
                  :post-population-density="terPopulationDensity" :post-wealth="terWealth" :post-area="terArea"
                  :post-oil-price="terOilPrice" :post-fuel-price="terFuelPrice" :post-has-reseller="terHasReseller"
                  :post-oil-well-price="terOilWellPrice " :post-oil-tower-price="terOilTowerPrice "
                  :post-rafinery-price="terRafineryPrice " :post-reseller-price="terResellerPrice"
                  :post-oil-sold="terOilSold " :post-fuel-sold="terFuelSold " :post-income="terIncome "
                  :post-oil-win="terOilWin " :post-fuel-processed="terFuelProcessed" :post-oil-well-count="terOWC"
                  :post-oil-tower-count="terOTC" :post-rafinery-count="terRC"
      ></area-popup>
    </v-dialog>
