</template>

<script>
import AreaPopup from '../components/Area.vue';
import {eventBus} from "@/main";

export default {
        name: 'WorldMap',
        components: {
            AreaPopup
        },
        data() {
          return {
              ters: [],
              territories:[],
              dialog: false,
              terId: '',
              terDesignation:'',
              terPopulation: '',
              terArea:'',
              terPopulationDensity:'',
              terWealth:'',
              terOilPrice: 0,
              terFuelPrice:0,
              terOilWellPrice:0,
              terOilTowerPrice:0,
              terRafineryPrice:0,
              terResellerPrice:0,
              terOilSold:0,
              terFuelSold:0,
              terIncome:0,
              terOilWin:0,
              terFuelProcessed:0,
              terOWC:0,
              terOTC:0,
              terRC:0,
              terHasReseller: 0
          }
        }, filters:{
        readableNumber: function (value) {
            if(value !== undefined)
                return value.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
        }
    },
        watch: {
            dialog(val) {
                if(val === true){eventBus.$emit('areaOpened', this.terId);}
                else {eventBus.$emit('areaClosed', this.terId);}
            }
        },
        //setup the world map on mounted lifecyclehook
        mounted: function () {
            this.setupMap();
            var that = this;
            var localMap = document.querySelector("#OtsMap");
            this.ters = localMap.childNodes[3].childNodes;
            this.ters.forEach(function (territory) {
                territory.style.fill = "lightgrey";
                territory.onmouseenter = function() {territory.style.fill = "grey";};
                territory.onmouseleave = function() {territory.style.fill = "lightgrey";};
                territory.onclick = function () {
                    let infoObject = that.getCorrespondingInfoObject(territory.id);
                    that.showPopup(infoObject);
                }
            });
        //Enable the NavBar
        this.$store.commit('unlockNavBar');
            this.$store.commit('lockDrawer') ;
            console.log("unlocked");
        },
        methods:{
            updateMapValues(){
                this.updateTers();
                var that = this;
                var localMap = document.querySelector("#OtsMap");
                this.ters = localMap.childNodes[3].childNodes;
                this.ters.forEach(function (territory) {
                    territory.onclick = function () {
                        let infoObject = that.getCorrespondingInfoObject(territory.id);
                        that.showPopup(infoObject);
                    }
                });
            },
            async showPopup(iO) {
                console.log(iO);
                let tempPlayer = await this.callUsername();
                //general information
                this.terId = iO.id;
                this.terDesignation = iO.designation;
                this.terPopulation = iO.population;
                this.terArea = iO.area;
                this.terPopulationDensity = iO.populationDensity;
                this.terWealth = iO.wealth;

                //additional info
                this.terOilWellPrice = iO.oilWellPrice.value;
                this.terOilTowerPrice = iO.oilTowerPrice.value;
                this.terRafineryPrice = iO.rafineryPrice.value;
                this.terResellerPrice = iO.resellerPrice.value;


                //user depended info
                this.terOWC = iO.playerOW[tempPlayer].value;
                this.terOTC = iO.playerOT[tempPlayer].value;
                this.terRC =iO.playerR[tempPlayer].value;
                this.terHasReseller = iO.playerRe[tempPlayer].value;
                console.log('pop'+this.terOWC);



                Object.entries(this.territories).forEach(([key, value])=>{
                    if(value.id === iO.id){
                        Object.entries(value.terOilPrice).forEach(([key, value])=>{
                            if(key === tempPlayer){
                                this.terOilPrice = value.value.toFixed();
                            }
                        });
                        Object.entries(value.terFuelPrice).forEach(([key, value])=>{
                            if(key === tempPlayer){
                                this.terFuelPrice = value.value.toFixed();
                            }
                        });
                        Object.entries(value.terOilSold).forEach(([key, value])=>{
                            if(key === tempPlayer){
                                this.terOilSold = value.value;
                            }
                        });
                        Object.entries(value.terFuelSold).forEach(([key, value])=>{
                            if(key === tempPlayer){
                                this.terFuelSold = value.value;
                            }
                        });
                        Object.entries(value.terIncome).forEach(([key, value])=>{
                            if(key === tempPlayer){
                                this.terIncome = value.value.toFixed();
                            }
                        });
                        Object.entries(value.terOilWin).forEach(([key, value])=>{
                            if(key === tempPlayer){
                                this.terOilWin = value.value;
                            }
                        });
                        Object.entries(value.terFuelProcessed).forEach(([key, value])=>{
                            if(key === tempPlayer){
                                this.terFuelProcessed = value.value;
                            }
                        });
                    }
                });

                //show area section
                this.dialog = true;
            },
            setupMap () {
                this.$http.get('http://localhost:8080/game/entities').then(
                    response =>{
                        console.log(response.url + " status:" + response.status);
                        //console.log(response);
                        this.$store.commit('setPlayers', response.body.players);
                        response.body.territories.forEach((bTer)=> {
                            this.territories.push(bTer);
                            //this.territories.push({id: bTer.id, designation: bTer.designation, population: bTer.population, populationDensity: bTer.populationDensity, wealth: bTer.wealth, area: bTer.area});
                        });
                       // console.log(this.territories);
                    }, error=>{
                        console.log(error);
                    });
            },
            updateTers(){
                this.$http.get('http://localhost:8080/game/entities').then(
                    response =>{
                        //console.log(response.url + " status:" + response.status);
                        console.log(response);
                        this.territories.forEach(ter=>{
                           response.body.territories.forEach(bTer=>{
                               //console.log(bTer);
                             if(ter.id === bTer.id){
                                // console.log(ter);
                                 ter = bTer;
                             }
                           });
                        });
                    },error=>{
                        console.log(error);
                    });
                //console.log("this.territories");
                //console.log(this.territories);
            },
            getTerritory:function (terId) {
                this.ters.forEach(function (territory) {
                   if(territory.id === terId)
                       return territory;
                });
            },
            getCorrespondingInfoObject:function (territoryId) {
                let result = null;
                this.territories.forEach(function(territory){
                    let ter1 = territory.id.trim();
                    let ter2 = territoryId.trim();
                    if(ter1 === ter2) {
                        result = territory;
                    }
                });
                return result;
            },
            callUsername () {
                return new Promise((resolve,reject)=> {
                    this.$http.get('http://localhost:8080/user/name').then(
               //     this.$http.get('http://falk.overzone-studio.de:8080/user/name').then(
                        response=>{
                            console.log(response.url + response.status);
                            this.$store.commit('setPlayers', response.bodyText);
                            resolve(response.bodyText);
                        },error=>{
                            console.log('error');
                            reject(error);
                        });
                });
            }
        },
        created(){
            eventBus.$on('safeState', (data)=>{
                console.log('safeState');
                console.log(data);
                this.$http.post('http://localhost:8080/game/action', data).then(
                 //   this.$http.post('http://falk.overzone-studio.de:8080/game/action', data).then(
                    response =>{
                        console.log(response);
                        this.updateMapValues();
                    }, error => {
                        console.log(error);
                    });
            });
        }
    }
</script>

